content.css:
.fader-container.design-compact {
  --cxolor-fill: rgba(77,95,114,1);
  --alpha-fill-off: 0;
  --alpha-fill-on: 0.1;
  --widget-padding: 0;
  --line-width: 0;
  --color-knob: var(--color-widget);
  --knob-size: 2;
}
.fader-container canvas {
   border-radius: 3rem;
    bxackground: var(--color-ui-background);

}
.strip {
  --color-background: var(--color-ui-foreground)
}
.matrix-container > inner > .widget {
  min-width:68rem!important;
}
.monitors-panel .matrix-container > inner > .widget {
  max-width:68rem!important;
}

.menu-container, .input-container, .button-container {
  --border-radius:3rem;
  --line-width:0;
}
:host {

  font-size:90%;
}

.strip-hidden {
  --color-widget: #555;
}
.strip-hidden .strip-options {
  opacity: 0.75;
}
.strip-options {
  position: absolute!important;
  top:4rem!important;
  right: 2rem!important;
  width:calc(50% - 6rem)!important;
  height:200rem!important;

}

.strip-meter {
  --line-width:1rem!important;
  --alpha-fill-on:1;
  --color-stroke: rgba(0,0,0,0.2);
}
.strip-meter canvas {
  background: rgba(0,0,0,0.2)!important;
}

.menu-container {
 --line-width:1rem;
 --color-stroke:rgba(0,0,0,0.25);
 --alpha-stroke:1;
}
.option-button {
 --color-fill: rgba(255,255,255,0.06);
 --alpha-fill-on: 1;
 --alpha-fill-off: 1;
 --color-text-on: var(--color-widget);
 --line-width:1rem;
 --color-stroke:rgba(0,0,0,0.5);
 --alpha-stroke:1;
}
.option-button.on {
  --color-fill: rgba(33,37,43,0.8)

}

