widgets.value:
JS{
var p
if (@{@{parent.variables.stripType}_dyn_expthresh_txt} < @{@{parent.variables.stripType}_dyn_compthresh_txt})
  p = [
      [ (1/@{@{parent.variables.stripType}_dyn_expratio_txt}) * (-100 - @{@{parent.variables.stripType}_dyn_expthresh_txt}) + @{@{parent.variables.stripType}_dyn_expthresh_txt}, -100],
      [ @{@{parent.variables.stripType}_dyn_expthresh_txt}, @{@{parent.variables.stripType}_dyn_expthresh_txt} ],
      [ @{@{parent.variables.stripType}_dyn_compthresh_txt}, @{@{parent.variables.stripType}_dyn_compthresh_txt} ],
      [ 30, (1/@{@{parent.variables.stripType}_dyn_compratio_txt}) * (30 - @{@{parent.variables.stripType}_dyn_compthresh_txt}) + @{@{parent.variables.stripType}_dyn_compthresh_txt}]
    ]
else p = [
      [ (1/@{@{parent.variables.stripType}_dyn_expratio_txt}) * (-100 - @{@{parent.variables.stripType}_dyn_expthresh_txt}) + @{@{parent.variables.stripType}_dyn_expthresh_txt}, -100],
      [ @{@{parent.variables.stripType}_dyn_expthresh_txt}, @{@{parent.variables.stripType}_dyn_compthresh_txt} ],
      [ 30, (1/@{@{parent.variables.stripType}_dyn_compratio_txt}) * (30 - @{@{parent.variables.stripType}_dyn_compthresh_txt}) + @{@{parent.variables.stripType}_dyn_compthresh_txt}]
    ]

return p
}

