widgets.onKeyboard:
if (locals.fIds[key]) {
  var id = locals.fIds[key]
  set(id, get(id) ? 0 : 1)
}

if (key == 's' && ctrl && globals.env.prod) {
  set('state_menu', 'save')
}

content.onTouch:
if (event.button === 1) {
  // middle click -> display help
  event.preventDefault = true
  if (event.type == 'start') {
    set('help', getProp(event.target, 'comments'))
    setTimeout(()=>{
      set('help', '')
    }, 3000)
  }
}
