.strip-matrix > inner > .widget {
  min-width:var(--strip-width)!important;
  max-width:var(--strip-max-width)!important;

}

JS{
  var vals = @{colors}
  var css = ''
  for (var i in vals) {
    css += ".color-menu .item:nth-child("+(parseInt(i)+1)+"){--color-fill:"+vals[i]+";background:"+vals[i]+";}\n"
  }
  return css
}

widgets.props:
JS{
var sourcesTypes = @{sources_types}||[],
    sourcesIds = @{sources_ids}||[],
    props = {}

props.widgetId = 'input_source_x'
props.props = {
  variables: {
    source: sourcesIds[$],
    sourcetype: sourcesTypes[$],
  }
}


props.visible = `
JS_{
return @_{hide_input_${sourcesTypes[$]}_${sourcesIds[$]}}
  && !@_{show_all} ? false:true  
}`

return props
}

widgets.onTouch:
if (event.type == 'start') {
  var sel = 'sel_output_' + getProp('parent', 'variables').output
  set(sel, getProp(sel, 'on'))
}

