.pips {
  position:absolute;
  top:0;left:0;right:0;bottom:0;
  pointer-events:none;
  margin:var(--widget-spacing);
  font-size: 80%;
}
.pips .v {
  position:absolute;
  top: 1rem;bottom:12rem;
  border-left:1rem solid grey;
  opacity:0.25;
}
.pips .v div {
  position:absolute;
  bottom:-20rem;
  left:-20rem;
  width:40rem;
  text-align:center;
}

.pips .h {
  position:absolute;
  left: 20rem;
  right: 1rem;
  border-top:1rem solid grey;
  opacity:0.25;
  padding-left:3rem;
  align-items: end;
  text-align:right;
  display:flex;
  line-height: 0rem;
  text-indent: -34rem;
}

