content.css:
.strip-matrix > inner > .widget {
  min-width:var(--strip-width)!important;
  max-width:var(--strip-max-width)!important;

}

JS{
  var vals = @{colors}
  var css = ''
  for (var i in vals) {
    css += ".color-menu .item:nth-child("+(parseInt(i)+1)+"){--color-fill:"+vals[i]+";background:"+vals[i]+";}\n"
  }
  return css
}

widgets.props:
JS{
var sourcesTypes = @{sources_types}||[],
    sourcesIds = @{sources_ids}||[],
    props = {}

props.widgetId = 'input_source_x'
props.props = {
  variables: {
    source: sourcesIds[$],
    sourcetype: sourcesTypes[$],
    options: sourcesTypes[$] == 'mic'?
      `options_input_mic_x`
      : false
  }
}

props.visible = `
JS_{
return @_{hide_input_${sourcesTypes[$]}_${sourcesIds[$]}}
  && !@_{show_all} ? false:true  
}`

return props
}

widgets.onValue:
var index = getProp('parent', 'variables').output
if (index % 2 == 1 && value == 1) {
  set('options_modal_output_'+index, 0)
}

widgets.css:
class: strip-meter;
class: ok-@{parent.variables.output};
JS{
var index = @{parent.variables.output},
    stereo = @{output_stereo}[index]
return stereo ? 'class:strip-meter-left;' :''
}

widgets.id:
JS{
var index = Math.floor(@{parent.variables.output}/2)*2,
    stereo = @{output_stereo}[index]
if (stereo) return 'output-meter:' + (index+1)
else return 'dummy-meter'
}

widgets.visible:
JS{
var index =  Math.floor(@{parent.variables.output}/2)*2,
    stereo = @{output_stereo}[index]
return stereo
}

widgets.props:
JS{
var props = {},
    output_ids = @{output_ids},
    output_stereo = @{output_stereo}

var id = output_ids[$]
var show = id % 2 == 0 || !output_stereo[$]

props.widgetId ='output_x' 
props.props = {
  id: 'outstripclone_' + $,
  variables: {
    output: id,
  },
}
props.visible = `
JS_{
return (${id} % 2 == 0 || !@_{stereo_output_${id}}) && (@_{hide_output_${id}}
    && !@_{show_all} 
  ? false : true)
}`

return props
}
